$(document).ready(function() {
	var bodyheight = jQuery(window).height() - 54;
	$("#pageframe").css("height", bodyheight);
	$(".mega-dropdown a[target='pageframe']").on("click", function() {
		$(".mega-dropdown").removeClass("open")
	});
	//_fastData;
	$("#j-sign-out").on("click", function() {
		// if (confirm("确定要退出吗？")) {
		// 	document.location.href = $(this).attr('action');
		// }
			$("#ap,#qp").css('display','block');
			$("#tex").text("确定要退出吗？");
			$("#btn1").click(function(){
				document.location.href = $("#j-sign-out").attr('action');
			});
			$("#del").click(function(){
				$("#ap,#qp").css('display','none');
			});
	});
	$("#j-sign-cash").on("click", function() {
		var n = 0;
		$.ajax({
			type : "post",
			url : $(this).attr('href'),
			async : false,
			success : function(data){
				if(data.status == 0){
					n = 1;
					$("#ap,#qp").css('display','block');
					$("#tex").text(data.info);
					$("#btn1").click(function(){
						$("#ap,#qp").css('display','none');
						$("#pageframe").attr('src',data.url).reload();
					});
					$("#del").click(function(){
						$("#ap,#qp").css('display','none');
					});
				}
			}
		});
		if(n == 0){
			$("#pageframe").attr('src',$(this).attr('href')).reload();
		}
		return false;
	})
});
$(window).resize(function() {
	var bodyheight = jQuery(window).height() - 54;
	$("#pageframe").css("height", bodyheight)
});